import React from 'react';

class PatientLogin extends React.Component {
	constructor(props) {
        super(props);

        this.state = { drug: [] };
	}

	handleClick = () => {
		console.log('this is:', this);
	}

 componentDidMount() 
 {
	fetch('http://localhost:32667/api/values', { method: 'GET' })
		.then(response => response.json())
		.then(data =>
			this.setState({
				drug: data,
				isLoading: false
			}))
	   .catch(error => this.setState({ error, isLoading: false }));
}

handleSubmit(e)
{
	if(this.refs.username.value === ''){
			alert('username is required');
			return;
	}

	if(this.refs.password.value === ''){
		alert('password is required');
		return;
	}

	console.log(this.refs.username.value);

		fetch('http://localhost:32667/api/values', 
		{mode: 'cors'}, 	
		{
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                Id: '3'                
            })
        }).then(res => {
            return console.log(res);
            }).catch(err => console.log(err));


	e.preventDefault();
}

render() {

	//console.log("--------------------->" + this.state.drug);

	const drugs = this.state.drug.map((item, i) => (
		<div>
			<h2>{item.Id}</h2>
		   <span>{item.Data}, {item.dosage}</span>
		</div>
	));

	return (
		<div>
		<div id="layout-content" className="layout-content-wrapper">
			<h1>Drugs List</h1>
			<div className="panel-list">{drugs}</div>			
		</div>

            <div>
                <h3> Login </h3>
                <form onSubmit={this.handleSubmit.bind(this)}> 
                    <div>
                        <label>username</label><br />
                        <input type="text" ref="username" />
                        
                    </div>
                    <div>
                        <label>password</label><br />
                        <input type="password" ref="password" />
                        <br />                     
                    </div> 
                    <input type="submit" value="submit" />                  
                    <br />
                </form>
            </div>
		</div>
	);
}
}


export default PatientLogin;
