import React from 'react';

class PatientLogin extends React.Component {
	constructor(props) {
        super(props);

        this.state = { drug: [] };
	}

	handleClick = () => 
	{
		console.log('this is:', this);
	}

  componentDidMount() {
	fetch('http://localhost:32667/api/values', { method: 'GET' })
		.then(response => response.json())
		.then(data =>
			this.setState({
				drug: data,
				isLoading: false
			}))
	   .catch(error => this.setState({ error, isLoading: false }));
}

handleSubmit(e)
{
	if(this.refs.title.value === ''){
			alert('title is required');
			return;
	}

	this.setState({newProject:{
		id: uuid.v4(),
		title : this.refs.title.value,
		category : this.refs.Category.value
	}}, function(){
		//console.log(this.state);
		this.props.addProject(this.state.newProject);
	});
	
	//console.log(this.ref.title.value);
	e.preventDefault();
}

render() {

	console.log("--------------------->" + this.state.drug);

	const drugs = this.state.drug.map((item, i) => (
		<div>
			<h2>{item.Id}</h2>
		   <span>{item.Data}, {item.dosage}</span>
		</div>
	));

	return (
		<div>
		<div id="layout-content" className="layout-content-wrapper">
			<h1>Drugs List</h1>
			<div className="panel-list">{drugs}</div>			
		</div>

            <div>
                <h3> Login </h3>

                <form onSubmit={this.handleSubmit.bind(this)}> 
                    <div>
                        <label>Title</label><br />
                        <input type="text" ref="username" />
                        
                    </div>
                    <div>
                        <label>Category</label><br />
                        <input type="text" ref="password" />
                        <br />
                     
                    </div> 
                    <input type="submit" value="submit" />                  
                    <br />
					 
                </form>
            </div>

		</div>
	);
}
}


export default PatientLogin;
